package Notes;
import static net.mindview.util.Print.*;
// exercise 9
public class MaxMin {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float maxf = java.lang.Float.MAX_VALUE;
		print("max value of float: " + maxf);
		float minf = java.lang.Float.MIN_VALUE;
		print("min value of float: " + minf);
		double maxd = java.lang.Double.MAX_VALUE;
		print("max value of double: " + maxd);
		double mind = java.lang.Double.MIN_VALUE;
		print("min value of double: " + mind);
	}

}
